import "./styles.css";
import axios from "axios";
import { useGet } from "./api";
import { Qiita } from "./model/qiita";
import { Grid, Button, Box, Paper } from "@material-ui/core";
import { useState, useEffect } from "react";
import React, { FunctionComponent } from "react";
import { jsx, css } from "@emotion/react";
import { Route, useParams } from "react-router";
import { useLocation } from "react-router-dom";

export const Content: FunctionComponent = () => {
  //const payload = useGet<Qiita>("https://qiita.com/api/v2/schema");
  const payload = useGet<Qiita>("https://qiita.com/api/v2/items");
  //const { load, item } = useGet("https://yahoo.co.jp");
  const location = useLocation();
  const style = {
    minWidth: 64, // 数値は"64px"のように、pxとして扱われます
    lineHeight: "32px",
    borderRadius: 4,
    border: "none",
    padding: "0 40px",
    color: "#fff",
    background: "#639"
  };
  useEffect(() => {
    payload.load();
  }, []);
  const submit = async () => {
    const token = "31d53c4f7df718fbb80c9c8f3bc3c87182121dbe";
    const config = {
      headers: { Authorization: `Bearer ${token}` }
    };
    const v = await axios.get<Qiita[]>(
      "https://qiita.com/api/v2/items",
      config
    );
    //https://hi1280.hatenablog.com/entry/2018/12/05/201004

    var q = v.data as Qiita[];
    alert(q[0].comments_count);
    // alert(JSON.stringify(v.data));
    // alert(JSON.stringify(v.data[0].rendered_body));
    // alert(JSON.stringify(v.data[0].comments_count));
    // alert(JSON.stringify(v.data[0].created_at));
    // alert("submit in Main.tsx");
    // alert(JSON.stringify(payload));
    // alert(JSON.stringify(v.data.rendered_body));
    // alert(JSON.stringify(v.data.coediting));
    // alert("submit in Main.tsx");
    //const =
    console.log(JSON.stringify(payload.item));
  };
  return (
    <div className="App">
      <Paper
        style={{ backgroundColor: "#373940" }}
        css={styles.card}
        elevation={3}
      >
        <Grid
          container
          direction={"column"}
          justify={"center"}
          alignItems={"center"}
          spacing={3}
        >
          <Grid>
            <Box css={styles.root}>
              <button
                style={style}
                onClick={() => {
                  submit();
                }}
              >
                Click me in Content.tsx
              </button>
            </Box>
          </Grid>
        </Grid>
        <Box> {payload.item?.body} </Box>
      </Paper>
    </div>
  );
};

const styles = {
  root: css`
    width: 100%;
    height: 100vh;
    display: flex;
    justify-content: center;
    align-items: center;
    background-color: #2f3136;
  `,
  card: css`
    display: inline-block;
    width: 450px;
    border-radius: 4px;
    padding: 32px;
  `,
  title: css`
    text-align: center;
    margin-bottom: 4px;
  `,
  subTitle: css`
    text-align: center;
  `,
  username: css`
    margin-bottom: 8px;
    text-align: start;
  `,
  usernameField: css`
    background-color: #31333a;
    color: #ffffff;
    caret-color: #ffffff;
    width: 100%;
  `,
  loginButton: css`
    width: 100%;
    height: 44px;
  `
};
