import { useState, useContext, useEffect } from "react";
import Modal from "@material-ui/core/Modal";
import { DropzoneDialog } from 'material-ui-dropzone';
import Button from '@material-ui/core/Button';
import axios from 'axios';




export  const UploadModal = ({ open2, handleClose }) => {
    const [open, setOpen] = useState(false);
    //const handleStateChange = ():void => setOpen(true);

    const postArticleImage = async (formData: FormData) => {
        try {
          const response = await axios.post('/api/post/image', formData, {
            headers: {'Content-Type': 'multipart/form-data'},
          });

          if (response.status !== 200) {
            throw new Error('Server Error');
          }

          return response.data;
        } catch (err) {
          throw err;
         }
     }

    return (
      <div>
                <Button variant="contained" color="primary" onClick={() => setOpen(true)}>
                    Add Image
                </Button>
                <DropzoneDialog
                    acceptedFiles={['image/*']}
                    cancelButtonText={"cancel"}
                    submitButtonText={"submit"}
                    maxFileSize={5000000}
                    open={open}
                    onClose={() => setOpen(false)}
                    onSave={(files) => {
                      axios.get(`http://localhost:3030/api/v1/photos`)
                        .then(res => {
                         console.log(res.data);
                        })
                    console.log('Files:', files);
                    setOpen(false);
                    }}
                    showPreviews={true}
                    showFileNamesInPreview={true}
                />
        </div>

    );

}
